mapper.select:
select file_url from image
where file_id = ANY(select file_id from review_file
where review_id = #{id});

mapper.insert:
INSERT INTO review (user_id, store_id, review_content,
rate)
VALUES (#{userId}, #{storeId}, #{reviewContent}, #{rate});

mapper.update:
UPDATE review
set views = views + 1
WHERE review_id = #{reviewId}
