mapper.select:
SELECT  club_name
FROM club
WHERE club_name = #{param1} and area_code = #{param2}

mapper.update:
update club
set club_name = #{clubName}, club_content = #{clubContent}, file_id = (select max(file_id) from image)
where club_id = #{clubId}

update club
set club_name = #{clubName}, club_content = #{clubContent}
where club_id = #{clubId}

update club
set user_id = #{userId}
where club_id = #{clubId}

mapper.select:
select c.club_id as clubId, u.nickname as nickname , c.user_id  as userId , c.type as type , c.created_at
 from club_member c , user u
where c.user_id = u.user_id and club_id = #{param1}

select c.club_id as clubId, u.nickname as nickname , c.user_id as userId , c.content, c.requested_at as requestedAt 
from club_member_waiting c, user u 
where c.user_id= u.user_id and club_id = #{clubId}

mapper.update:
update club
set member_number = member_number + 1
where club_id = #{clubId}

update club
set member_number = member_number - 1
where club_id = #{clubId}

mapper.select:
select c.club_id, club_name, member_number, club_content, is_open, c.created_at, area_code, c.user_id, file_id
from club c, club_member m
where m.user_id = #{userId} and m.club_id = c.club_id;

select c.club_id, club_name, member_number, club_content, is_open, c.created_at, area_code, c.user_id, file_id
from club c, club_member_waiting m
where m.user_id = #{userId} and m.club_id = c.club_id;
