mapper.select:
SELECT user_id as userId, nickname, password, email
FROM user
WHERE user_id = #{userId}
AND email = #{email}

SELECT  user_id as userId
FROM user
WHERE user_id = #{userId}

SELECT  nickname
FROM user
WHERE nickname = #{nickname}

SELECT  email
FROM user
WHERE email = #{email}

SELECT  user_address_code as addressCode
FROM user_address
WHERE user_id = #{userId}
